import React, { useState, useEffect } from 'react';
import SnowContainer from './components/SnowContainer';
import VisitorsList from './components/VisitorsList';
import Loader from './components/Loader';
import MusicControl from './components/MusicControl';
import GreetingForm from './components/GreetingForm';
import GreetingMessage from './components/GreetingMessage';
import styles from './App.module.css';

function App() {
  const [loading, setLoading] = useState(true);
  const [greeting, setGreeting] = useState('');
  const [visitors, setVisitors] = useState([]);

  useEffect(() => {
    const storedVisitors = JSON.parse(localStorage.getItem('christmasVisitors') || '[]');
    setVisitors(storedVisitors);

    // Simuler le chargement
    setTimeout(() => setLoading(false), 5000);
  }, []);

  const handleSubmit = (name) => {
    const formattedName = name.charAt(0).toUpperCase() + name.slice(1).toLowerCase();
    setGreeting(formattedName);

    if (!visitors.includes(formattedName)) {
      const newVisitors = [...visitors, formattedName];
      setVisitors(newVisitors);
      localStorage.setItem('christmasVisitors', JSON.stringify(newVisitors));
    }
  };

  if (loading) return <Loader />;

  return (
    <div className={styles.app}>
      <MusicControl />
      <SnowContainer />

      <h1 className={styles.title}>✨ Joyeux Noël ! ✨</h1>

      <GreetingForm onSubmit={handleSubmit} />

      {greeting && <GreetingMessage name={greeting} />}

      <VisitorsList visitors={visitors} />
    </div>
  );
}

export default App;
